fillRestaurantHoursHTML = function fillRestaurantHoursHTML() {
  var operatingHours = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : self.restaurant.operating_hours;

  var hours = document.getElementById('restaurant-hours');
  hours.setAttribute('aria-label', 'Restaurant opening hours.');
  for (var key in operatingHours) {
    var row = document.createElement('tr');

    var day = document.createElement('td');
    day.innerHTML = key;
    row.appendChild(day);

    var time = document.createElement('td');
    time.innerHTML = operatingHours[key];
    row.appendChild(time);

    hours.appendChild(row);
  }
};

/**
 * Create all reviews HTML and add them to the webpage.
 */
fillReviewsHTML = function fillReviewsHTML() {
  var reviews = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : self.reviews;

  var container = document.getElementById('reviews-container');
  var title = document.createElement('h3');
  title.innerHTML = 'Reviews';
  container.appendChild(title);
  if (!reviews) {
    var noReviews = document.createElement('p');
    noReviews.innerHTML = 'No reviews yet!';
    container.appendChild(noReviews);
    return;
  }
  var ul = document.getElementById('reviews-list');
  ul.innerHTML = "";
  reviews.forEach(function (review) {
    ul.appendChild(createReviewHTML(review));
  });
  container.appendChild(ul);
};

/**
 * Rebuild the reviews HTML
 */
rebuildReviews = function rebuildReviews() {
  var reviews = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : self.reviews;

  var id = getParameterByName('id');
  var container = document.getElementById('reviews-container');
  var ul = document.getElementById('reviews-list');

  DBHelper.fetchReviewsById(id, function (error, reviews) {
    self.reviews = reviews;
    ul.innerHTML = "";
    reviews.forEach(function (review) {
      ul.appendChild(createReviewHTML(review));
    });
    container.appendChild(ul);
  });
};

/**
 * Create review HTML and add it to the webpage.
 */
createReviewHTML = function createReviewHTML(review) {
  var li = document.createElement('li');
  li.setAttribute('tabindex', '0');
  var div = document.createElement('div');
  div.className = "name-date";
  div.setAttribute('aria-label', 'Reviewer name');

  var name = document.createElement('p');
  name.className = "name";
  name.innerHTML = review.name;

  var date = document.createElement('p');
  date.className = "date";
  date.setAttribute('aria-label', 'Review date');
  var reviewDate = new Date(review.createdAt);
  date.innerHTML = reviewDate.toDateString();

  div.appendChild(name);
  div.appendChild(date);
  li.appendChild(div);

  var divp = document.createElement('div');
  divp.className = "content";

  var rating = document.createElement('p');
  rating.className = "rating";
  rating.setAttribute('aria-label', 'Review rating');
  rating.innerHTML = 'Rating: ' + review.rating;
  li.appendChild(rating);

  var comments = document.createElement('p');
  comments.innerHTML = review.comments;

  divp.appendChild(comments);
  li.appendChild(divp);

  return li;
};

/**
 * Add restaurant name to the breadcrumb navigation menu
 */
fillBreadcrumb = function fillBreadcrumb() {
  var restaurant = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : self.restaurant;

  var breadcrumb = document.getElementById('breadcrumb');
  var li = document.createElement('li');
  li.setAttribute('aria-current', 'page');
  li.innerHTML = restaurant.name;
  breadcrumb.appendChild(li);
};

/**
 * Get a parameter by name from page URL.
 */
getParameterByName = function getParameterByName(name, url) {
  if (!url) url = window.location.href;
  name = name.replace(/[\[\]]/g, '\\$&');
  var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
      results = regex.exec(url);
  if (!results) return null;
  if (!results[2]) return '';
  return decodeURIComponent(results[2].replace(/\+/g, ' '));
};

/**
 * Get reviews for current restaurant from page URL.
 */
fetchReviewsFromURL = function fetchReviewsFromURL(callback) {
  if (self.reviews) {
    // restaurant already fetched!
    callback(null, self.reviews);
    return;
  };

  var id = getParameterByName('id');
  if (!id) {
    // no id found in URL
    error = 'No restaurant id in URL';
    callback(error, null);
  } else {
    DBHelper.fetchReviewsById(id, function (error, reviews) {
      self.reviews = reviews;
      if (!reviews) {
        console.error(error);
        return;
      }

      // fill reviews
      fillReviewsHTML();
    });
  }
};

